var label = new Box()
$.__.$('#').append( label.$base )

label.$base.data( 'state', new List( {
} ) )

label.$base.data( 'config', function() {
    var str = 'Hello World'
    var $txt = this.$txt = this.$txt || $.__.$('<text/>')
    this.time = this.time || {}
    if( ! this.time.start ) {
        this.time.start = ( new Date() ).getTime()
        var $caller = this.$caller = this.$caller || $( this.__.get( 'caller' ) )
        $caller.append( $txt )
        var bboxes = []
        var max = this.max = { width : 0, height : 0 }
        for( var i = 1; i <= str.length; i++ ) {
            $txt.text( str.substring( 0, i ) )
            bboxes[ i ] = $txt.get(0).getBBox()
            ;[ 'width', 'height' ].each( function( prop ) {
                max[ prop ] = Math.max( max[ prop ], bboxes[ i ][ prop ] )
            } )
        }
        console.log( max )
        this.bboxes = bboxes
    }
    console.log(this.time.start)
} )

function config( ) {
    var $this = $(this)
    var state = $this.data( 'state' )
    if( state && state.__ && typeof state.__.set == 'function' ) {
        state.__.set( 'caller', this )
    }
    state = state || {}
    var cfg = $this.data( 'config' )
    if( typeof cfg == 'function'
        && cfg != undefined ) {
        cfg.apply( state, arguments )
    }
}
$.__.$('#').each( function traverse( ) {
    config.apply( this, arguments )
    $(this).children().each( traverse ) 
} )

